/**
 * Detect error generated by Ember Data when record has been deleted in current session
 * and we try to get it using `store.findRecord`.
 *
 * @author Jakub Liput
 * @copyright (C) 2021 ACK CYFRONET AGH
 * @license This software is released under the MIT license cited in 'LICENSE.txt'.
 */

export default function isDeletedEmberError(error) {
  return error && error.message && error.message.match(/root\.deleted\.saved/);
}
